mapper.select:
SELECT COUNT(*) FROM expense.expense LEFT JOIN fixed_expense ON expense.id = fixed_expense.expense_id LEFT JOIN variable_expense ON expense.id = variable_expense.expense_id LEFT JOIN attribute ON attribute.id = fixed_expense.attribute_id OR attribute.id = variable_expense.attribute_id
date_trunc('month', expense.payment_date) = to_timestamp(#{criteria.yearMonth}, 'YYYY-MM')
AND date_trunc('year', expense.payment_date) = to_timestamp(#{criteria.year}, 'YYYY')
AND category = #{criteria.expenseCategory}
AND category = #{criteria.expenseAttributeName.value}

SELECT expense.id, expense.description, expense.price, expense.payment_date, attribute.id AS attribute_id, attribute.category, attribute.name AS attribute_name FROM expense.expense LEFT JOIN fixed_expense ON expense.id = fixed_expense.expense_id LEFT JOIN variable_expense ON expense.id = variable_expense.expense_id LEFT JOIN attribute ON attribute.id = fixed_expense.attribute_id OR attribute.id = variable_expense.attribute_id
date_trunc('month', expense.payment_date) = to_timestamp(#{criteria.yearMonth}, 'YYYY-MM')
AND date_trunc('year', expense.payment_date) = to_timestamp(#{criteria.year}, 'YYYY')
AND category = #{criteria.expenseCategory}
AND category = #{criteria.expenseAttributeName.value}
ORDER BY attribute.category DESC, expense.payment_date DESC LIMIT #{criteria.perPage} OFFSET #{criteria.offset}
