mapper.select:
SELECT income.id, income.description, income.amount, income.receive_date, attribute.id AS attribute_id, attribute.name AS attribute_name FROM expense.income LEFT JOIN income_attribute ON attribute.id = income.attribute_id
date_trunc('year', income.receive_date) = to_timestamp(#{criteria.year}, 'YYYY')
ORDER BY expense.receive_date DESC LIMIT #{criteria.perPage} OFFSET #{criteria.offset}
